  /*******************
   * UTILITY FUNCTIONS
   *******************/
  flush: function() {
    this.ref().flush.apply(this.ref(), arguments);
    return this;
  },

  autoFlush: function() {
    this.ref().autoFlush.apply(this.ref(), arguments);
    return this;
  },

